from collections import deque import math
class Cache:
def __init__(self, location, movies, K): self.loc = location[0] self.K = K self.dq = deque() self.movieList = dict() for i in movies: self.movieList[i] = False def exist(self, movie): try: return self.movieList[movie] except: return None def add(self, movie): if len(self.dq) == self.K: temp = self.dq.pop() self.movieList[temp] = False self.dq.appendleft(movie) self.movieList[movie] = True return  class CachingMechanism(object): &ldquo;&ldquo;&ldquo;A Python emulator of a caching mechanism for movies.
